('weePay Payment', 'weePay', 'destek@weepay.co', '3.0.0.0', 'http://weepay.co/', '<modification>\n	<id></id>\n	<name>weePay Payment</name>\n	<code>weepay_payment</code>\n	<version>3.0.0.0</version>\n	<author>destek@weepay.co</author>\n	<link>http://weepay.co</link>\n	<file path="admin/controller/common/header.php">\n		<operation>\n			<search  index="0"><![CDATA[getStores(]]></search>\n			<add position="before"><![CDATA[	   \n			$this->load->language(\'extension/payment/weepay_payment\');\n            $this->load->model(\'extension/payment/weepay_payment\');\n			$versionweePay = "3.0.0.0";\n            $versionCheckweePay = $this->model_extension_payment_weepay_payment->versionCheck(VERSION, $versionweePay);\n\n            if (!empty($versionCheckweePay[\'version_status\']) AND $versionCheckweePay[\'version_status\'] == \'1\') {\n                $data[\'error_version\'] = $this->language->get(\'entry_error_version\');\n				$data[\'alerts\']= 1;\n				$data[\'version_status_weepay\']= $versionCheckweePay[\'version_status\'];\n                $data[\'weepay_or_text\'] = $this->language->get(\'entry_weepay_or_text\');\n                $data[\'weepay_update_button\'] = $this->language->get(\'entry_weepay_update_button\');\n                $version_updatable = $versionCheckweePay[\'new_version_id\'];\n                $data[\'version_update_link\'] = $this->url->link(\'extension/payment/weepay_payment/update\', \'token=\' . $this->session->data[\'token\'] . "&version=$version_updatable", true);\n            }else{\n			$data[\'version_status_weepay\']=\'0\';	\n			}                \n]]></add>\n		</operation>\n	</file>\n	\n<file path="admin/view/template/common/header.tpl">\n		<operation>\n			<search  index="0"><![CDATA[<ul class="nav pull-right">]]></search>\n			<add position="after"><![CDATA[\n               <?php if($version_status_weepay > 0) { ?>   <li class="dropdown"><a class="dropdown-toggle" data-toggle="dropdown"><span class="label label-danger pull-left"><?php echo $version_status_weepay; ?></span> <i class="fa fa-upload fa-lg"></i></a>\n	   <ul class="dropdown-menu dropdown-menu-right alerts-dropdown">\n	    <li class="dropdown-header"><?php echo $error_version ?><a href=\'<?php echo $version_update_link ?>\'><?php echo $weepay_update_button ?></a><?php echo $weepay_or_text ?></li>\n	   </ul>\n	   \n	</li>\n         <?php } ?>          \n]]></add>\n		</operation>\n	</file>\n	\n	\n</modification>', 1, now());